var chickletColours = [ 'maroon-back', 'light-green-back', 'dark-orange-back', 'gold-back', 'red-back'];
var chickletSquareCount = 0;

$('.chicklet').each(function(index, value){
  $(this).addClass(chickletColours[chickletSquareCount % chickletColours.length]);
  chickletSquareCount++;
})

var colours = [ 'maroon-back', 'light-green-back', 'dark-orange-back', 'gold-back', 'red-back'];
var squareCount = 0;

$('.block-content').each(function(index, value){
  $(this).addClass(colours[squareCount % colours.length]);
  squareCount++;
})
