_wpv_settings.filter_meta_html_js:
var notFiltered = $( '.js-wpv-filter-trigger-delayed' ).is(function() {
     return $(this).val() != 0;
  });

if ( notFiltered == true ) {
	$('.reset-btn').removeClass( "hidden" );
  	$('.explore-main-filter').addClass( "hidden").html('<div class="row"><div class="container"><div class="row"> <h2>Search Results:</h2> </div></div></div>');
}
$('.search-bar').attr('id', 'search-translations-bar');

